mapper.update:
update tenant
set name          = #{name},
    status_code   = #{status.code},
    last_updated_at  = #{lastUpdatedAt},
    last_updated_by  = #{lastUpdatedBy}
where id = #{id}

mapper.select:
select id, name, status_code, created_at, created_by, last_updated_at, last_updated_by
from tenant
where id = #{id}
